import ExpenseItem from "./ExpenseItem";
import items from "../items";
import "./ExpenseItem.css";
import ExpensesFilter from "./ExpensesFilter";
import { useState } from "react";
import NewExpense from "../NewExpenses/NewExpenses";

function Expenses() {
  const [filteredYear, setFilteredYear] = useState("2021");
  const [expenses, setExpenses] = useState(items);

  const filteredExpenses = expenses.filter((expense) => {
    return expense.date.getFullYear().toString() === filteredYear;
  });

  console.log(filteredExpenses);

  const chosenYear = (enteredYear) => {
    setFilteredYear(enteredYear);
  };

  const addExpenseHandler = (expense) => {
    console.log(expense);
    setExpenses((prevExpense) => {
      return [expense, ...prevExpense];
    });
  };

  return (
    <div className="expenses">
      <div>
        <ExpensesFilter selected={filteredYear} onChangeYear={chosenYear} />
        <NewExpense onAddExpenseData={addExpenseHandler} />
      </div>
      {filteredExpenses.length === 0 && (
        <h1 className="expenses">There is no expenses in this year</h1>
      )}
      {filteredExpenses.map((expense) => {
        return (
          <ExpenseItem
            key={expense.id}
            date={expense.date}
            title={expense.title}
            amount={expense.amount}
          />
        );
      })}
    </div>
  );
}

export default Expenses;
